import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));


// Add CORS headers for development  
app.use((req, res, next) => {
  const origin = req.headers.origin;
  res.header('Access-Control-Allow-Origin', origin || '*');
  res.header('Access-Control-Allow-Methods', 'GET,PUT,POST,DELETE,OPTIONS,PATCH');
  res.header('Access-Control-Allow-Headers', 'X-Requested-With,content-type,Authorization');

  // Handle preflight requests
  if (req.method === 'OPTIONS') {
    return res.sendStatus(200);
  }
  next();
});

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    if (!res.headersSent) {
      res.status(status).json({ message });
    }
    console.error("Server error:", err);
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { PasswordService } from "./services/password-service";
import { PhishingService } from "./services/phishing-service";
import { PortService } from "./services/port-service";
import { KeyloggerService } from "./services/keylogger-service";
import { FileIntegrityService } from "./services/file-integrity-service";
import { 
  passwordAnalysisRequestSchema,
  phishingAnalysisRequestSchema,
  portScanRequestSchema,
  fileMonitorRequestSchema
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  const passwordService = new PasswordService();
  const phishingService = new PhishingService();
  const portService = new PortService();
  const keyloggerService = new KeyloggerService();
  const fileIntegrityService = new FileIntegrityService();


  // Password Analysis
  app.post("/api/security/password-analysis", async (req, res) => {
    try {
      const { password } = passwordAnalysisRequestSchema.parse(req.body);
      const analysis = passwordService.analyzePassword(password);
      res.json(analysis);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Phishing URL Detection
  app.post("/api/security/phishing-analysis", async (req, res) => {
    try {
      const { url } = phishingAnalysisRequestSchema.parse(req.body);
      const analysis = phishingService.analyzeUrl(url);
      res.json(analysis);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Port Scanning
  app.post("/api/security/port-scan", async (req, res) => {
    try {
      const { target, portRange } = portScanRequestSchema.parse(req.body);
      const result = await portService.scanPorts(target, portRange);
      res.json(result);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Quick port scan for common ports
  app.post("/api/security/port-scan-quick", async (req, res) => {
    try {
      const { target } = req.body;
      if (!target) {
        return res.status(400).json({ message: "Target is required" });
      }

      const result = await portService.quickScan(target);
      res.json(result);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Keylogger Detection
  app.post("/api/security/keylogger-scan", async (req, res) => {
    try {
      const result = await keyloggerService.detectKeyloggers();
      res.json(result);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Terminate suspicious process
  app.post("/api/security/terminate-process", async (req, res) => {
    try {
      const { pid } = req.body;
      if (!pid) {
        return res.status(400).json({ message: "Process ID is required" });
      }

      const success = await keyloggerService.terminateProcess(parseInt(pid));
      res.json({ success });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // File Integrity Monitoring
  app.post("/api/security/file-integrity-init", async (req, res) => {
    try {
      const { directory, recursive } = fileMonitorRequestSchema.parse(req.body);
      await fileIntegrityService.initializeBaseline(directory, recursive);

      const baselineInfo = fileIntegrityService.getBaselineInfo();
      res.json({ 
        message: "Baseline initialized successfully",
        ...baselineInfo
      });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/security/file-integrity-check", async (req, res) => {
    try {
      const { directory, recursive } = fileMonitorRequestSchema.parse(req.body);
      const result = await fileIntegrityService.checkIntegrity(directory, recursive);
      res.json(result);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/security/file-integrity-update", async (req, res) => {
    try {
      const { directory, recursive } = fileMonitorRequestSchema.parse(req.body);
      await fileIntegrityService.updateBaseline(directory, recursive);

      const baselineInfo = fileIntegrityService.getBaselineInfo();
      res.json({ 
        message: "Baseline updated successfully",
        ...baselineInfo
      });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });


  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type ScanResult, type InsertScanResult, type MonitoredFile, type InsertMonitoredFile, users, scanResults, monitoredFiles } from "@shared/schema";
import { db } from "./db";
import { eq, and } from "drizzle-orm";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  createScanResult(scanResult: InsertScanResult): Promise<ScanResult>;
  getScanResults(type?: string): Promise<ScanResult[]>;
  getUserScanResults(userId: string, type?: string): Promise<ScanResult[]>;

  createMonitoredFile(file: InsertMonitoredFile): Promise<MonitoredFile>;
  getMonitoredFiles(): Promise<MonitoredFile[]>;
  updateMonitoredFile(id: string, updates: Partial<MonitoredFile>): Promise<MonitoredFile | undefined>;
  deleteMonitoredFile(id: string): Promise<boolean>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async createScanResult(insertScanResult: InsertScanResult): Promise<ScanResult> {
    const [scanResult] = await db
      .insert(scanResults)
      .values({
        ...insertScanResult,
        score: insertScanResult.score ?? null,
      })
      .returning();
    return scanResult;
  }

  async getScanResults(type?: string): Promise<ScanResult[]> {
    const query = db.select().from(scanResults);
    const results = type 
      ? await query.where(eq(scanResults.type, type))
      : await query;

    return results.sort((a, b) => (b.timestamp?.getTime() || 0) - (a.timestamp?.getTime() || 0));
  }

  async getUserScanResults(userId: string, type?: string): Promise<ScanResult[]> {
    const whereCondition = type 
      ? and(eq(scanResults.userId, userId), eq(scanResults.type, type))
      : eq(scanResults.userId, userId);

    const results = await db.select().from(scanResults).where(whereCondition);

    return results.sort((a, b) => (b.timestamp?.getTime() || 0) - (a.timestamp?.getTime() || 0));
  }

  async createMonitoredFile(insertFile: InsertMonitoredFile): Promise<MonitoredFile> {
    const [file] = await db
      .insert(monitoredFiles)
      .values({
        ...insertFile,
        isActive: true,
      })
      .returning();
    return file;
  }

  async getMonitoredFiles(): Promise<MonitoredFile[]> {
    return await db
      .select()
      .from(monitoredFiles)
      .where(eq(monitoredFiles.isActive, true));
  }

  async updateMonitoredFile(id: string, updates: Partial<MonitoredFile>): Promise<MonitoredFile | undefined> {
    const [updated] = await db
      .update(monitoredFiles)
      .set(updates)
      .where(eq(monitoredFiles.id, id))
      .returning();
    return updated || undefined;
  }

  async deleteMonitoredFile(id: string): Promise<boolean> {
    const result = await db
      .delete(monitoredFiles)
      .where(eq(monitoredFiles.id, id));
    return (result.rowCount ?? 0) > 0;
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, integer, boolean, jsonb, index } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table for session persistence
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  email: text("email").notNull().unique(),
  name: text("name").notNull(),
  password: text("password").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const scanResults = pgTable("scan_results", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  type: varchar("type").notNull(), // 'password', 'phishing', 'port', 'keylogger', 'file_integrity'
  target: text("target").notNull(),
  result: text("result").notNull(), // JSON string
  score: integer("score"),
  userId: varchar("user_id"), // Optional - null for guest scans
  timestamp: timestamp("timestamp").defaultNow(),
});

export const monitoredFiles = pgTable("monitored_files", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  filePath: text("file_path").notNull(),
  hash: text("hash").notNull(),
  size: integer("size").notNull(),
  lastModified: timestamp("last_modified").notNull(),
  isActive: boolean("is_active").default(true),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  email: true,
  name: true,
  password: true,
});

export const signupRequestSchema = z.object({
  name: z.string().min(2, 'Name must be at least 2 characters'),
  email: z.string().email('Please enter a valid email address'),
  password: z.string().min(8, 'Password must be at least 8 characters'),
  confirmPassword: z.string(),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

export const loginRequestSchema = z.object({
  email: z.string().email('Please enter a valid email address'),
  password: z.string().min(1, 'Password is required'),
});

export const insertScanResultSchema = createInsertSchema(scanResults).pick({
  type: true,
  target: true,
  result: true,
  score: true,
  userId: true,
});

export const insertMonitoredFileSchema = createInsertSchema(monitoredFiles).pick({
  filePath: true,
  hash: true,
  size: true,
  lastModified: true,
});

export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type ScanResult = typeof scanResults.$inferSelect;
export type InsertScanResult = z.infer<typeof insertScanResultSchema>;
export type MonitoredFile = typeof monitoredFiles.$inferSelect;
export type InsertMonitoredFile = z.infer<typeof insertMonitoredFileSchema>;

// API Request/Response schemas
export const passwordAnalysisRequestSchema = z.object({
  password: z.string().min(1),
});

export const phishingAnalysisRequestSchema = z.object({
  url: z.string().url(),
});

export const portScanRequestSchema = z.object({
  target: z.string().min(1),
  portRange: z.string().optional().default("1-1000"),
});

export const fileMonitorRequestSchema = z.object({
  directory: z.string().min(1),
  recursive: z.boolean().default(true),
});

export type PasswordAnalysisRequest = z.infer<typeof passwordAnalysisRequestSchema>;
export type PhishingAnalysisRequest = z.infer<typeof phishingAnalysisRequestSchema>;
export type PortScanRequest = z.infer<typeof portScanRequestSchema>;
export type FileMonitorRequest = z.infer<typeof fileMonitorRequestSchema>;
export type SignupRequest = z.infer<typeof signupRequestSchema>;
export type LoginRequest = z.infer<typeof loginRequestSchema>;

