import fs from 'fs/promises';
import path from 'path';
import crypto from 'crypto';

export interface FileIntegrityResult {
  directory: string;
  totalFiles: number;
  changes: FileChange[];
  statistics: {
    modified: number;
    added: number;
    deleted: number;
    unchanged: number;
  };
  riskAssessment: {
    level: 'low' | 'medium' | 'high' | 'critical';
    criticalChanges: number;
    suspiciousPatterns: string[];
  };
  lastScan: Date;
}

export interface FileChange {
  type: 'added' | 'modified' | 'deleted';
  filePath: string;
  oldHash?: string;
  newHash?: string;
  size?: number;
  permissions?: string;
  riskLevel: 'low' | 'medium' | 'high' | 'critical';
  reasons?: string[];
  timestamp: Date;
}

export interface MonitoredFileInfo {
  path: string;
  hash: string;
  size: number;
  lastModified: Date;
}

export class FileIntegrityService {
  private baseline: Map<string, MonitoredFileInfo> = new Map();

  async initializeBaseline(directory: string, recursive: boolean = true): Promise<void> {
    const files = await this.scanDirectory(directory, recursive);
    this.baseline.clear();

    for (const file of files) {
      this.baseline.set(file.path, file);
    }
  }

  async checkIntegrity(directory: string, recursive: boolean = true): Promise<FileIntegrityResult> {
    const currentFiles = await this.scanDirectory(directory, recursive);
    const changes: FileChange[] = [];
    const statistics = {
      modified: 0,
      added: 0,
      deleted: 0,
      unchanged: 0
    };

    // Create a map of current files for easier lookup
    const currentFileMap = new Map<string, MonitoredFileInfo>();
    currentFiles.forEach(file => currentFileMap.set(file.path, file));

    // Check for modifications and deletions
    for (const [filePath, baselineFile] of Array.from(this.baseline.entries())) {
      const currentFile = currentFileMap.get(filePath);

      if (!currentFile) {
        // File was deleted
        const riskLevel = this.assessFileRisk(filePath, 'deleted');
        changes.push({
          type: 'deleted',
          filePath,
          oldHash: baselineFile.hash,
          riskLevel,
          reasons: this.getChangeReasons(filePath, 'deleted'),
          timestamp: new Date()
        });
        statistics.deleted++;
      } else if (currentFile.hash !== baselineFile.hash) {
        // File was modified
        const riskLevel = this.assessFileRisk(filePath, 'modified');
        changes.push({
          type: 'modified',
          filePath,
          oldHash: baselineFile.hash,
          newHash: currentFile.hash,
          size: currentFile.size,
          riskLevel,
          reasons: this.getChangeReasons(filePath, 'modified'),
          timestamp: new Date()
        });
        statistics.modified++;
      } else {
        statistics.unchanged++;
      }
    }

    // Check for new files
    for (const currentFile of currentFiles) {
      if (!this.baseline.has(currentFile.path)) {
        const riskLevel = this.assessFileRisk(currentFile.path, 'added');
        changes.push({
          type: 'added',
          filePath: currentFile.path,
          newHash: currentFile.hash,
          size: currentFile.size,
          riskLevel,
          reasons: this.getChangeReasons(currentFile.path, 'added'),
          timestamp: new Date()
        });
        statistics.added++;
      }
    }

    // Assess risk level of changes
    const riskAssessment = this.assessRisk(changes);

    return {
      directory,
      totalFiles: currentFiles.length,
      changes: changes.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime()),
      statistics,
      riskAssessment,
      lastScan: new Date()
    };
  }

  async updateBaseline(directory: string, recursive: boolean = true): Promise<void> {
    await this.initializeBaseline(directory, recursive);
  }

  private async scanDirectory(directory: string, recursive: boolean): Promise<MonitoredFileInfo[]> {
    const files: MonitoredFileInfo[] = [];

    try {
      const entries = await fs.readdir(directory, { withFileTypes: true });

      for (const entry of entries) {
        const fullPath = path.join(directory, entry.name);

        if (entry.isFile()) {
          try {
            const fileInfo = await this.getFileInfo(fullPath);
            files.push(fileInfo);
          } catch (error) {
            console.warn(`Skipping file ${fullPath}: ${error}`);
          }
        } else if (entry.isDirectory() && recursive) {
          // Skip hidden directories and common system directories
          if (!entry.name.startsWith('.') && 
              !['node_modules', '__pycache__', '.git', '.svn'].includes(entry.name)) {
            const subdirFiles = await this.scanDirectory(fullPath, recursive);
            files.push(...subdirFiles);
          }
        }
      }
    } catch (error) {
      console.error(`Error scanning directory ${directory}:`, error);
    }

    return files;
  }

  private async getFileInfo(filePath: string): Promise<MonitoredFileInfo> {
    const stats = await fs.stat(filePath);
    const hash = await this.calculateFileHash(filePath);

    return {
      path: filePath,
      hash,
      size: stats.size,
      lastModified: stats.mtime
    };
  }

  private async calculateFileHash(filePath: string): Promise<string> {
    const data = await fs.readFile(filePath);
    return crypto.createHash('sha256').update(data).digest('hex');
  }

  async getFileContent(filePath: string): Promise<string> {
    try {
      const data = await fs.readFile(filePath, 'utf-8');
      return data;
    } catch (error) {
      throw new Error(`Cannot read file: ${error}`);
    }
  }

  async isFileText(filePath: string): Promise<boolean> {
    try {
      const buffer = await fs.readFile(filePath);
      const sample = buffer.slice(0, 1024);

      // Check for null bytes (common in binary files)
      for (let i = 0; i < sample.length; i++) {
        if (sample[i] === 0) return false;
      }

      // Check if most characters are printable ASCII
      let printableCount = 0;
      for (let i = 0; i < sample.length; i++) {
        const char = sample[i];
        if ((char >= 32 && char <= 126) || char === 9 || char === 10 || char === 13) {
          printableCount++;
        }
      }

      return printableCount / sample.length > 0.7;
    } catch {
      return false;
    }
  }

  getBaselineInfo(): { totalFiles: number; directories: string[] } {
    const directories = new Set<string>();
    for (const filePath of Array.from(this.baseline.keys())) {
      directories.add(path.dirname(filePath));
    }

    return {
      totalFiles: this.baseline.size,
      directories: Array.from(directories).sort()
    };
  }

  exportBaseline(): MonitoredFileInfo[] {
    return Array.from(this.baseline.values());
  }

  importBaseline(baseline: MonitoredFileInfo[]): void {
    this.baseline.clear();
    baseline.forEach(file => this.baseline.set(file.path, file));
  }

  private assessFileRisk(filePath: string, changeType: 'added' | 'modified' | 'deleted'): 'low' | 'medium' | 'high' | 'critical' {
    const fileName = path.basename(filePath).toLowerCase();
    const fileExt = path.extname(filePath).toLowerCase();
    const dirPath = path.dirname(filePath).toLowerCase();

    // Critical risk files
    if (fileName.includes('password') || fileName.includes('secret') || fileName.includes('key')) {
      return 'critical';
    }
    if (['.exe', '.dll', '.bat', '.cmd', '.ps1', '.sh'].includes(fileExt)) {
      return 'critical';
    }
    if (dirPath.includes('system32') || dirPath.includes('windows') || dirPath.includes('/etc/') || dirPath.includes('/bin/')) {
      return 'critical';
    }

    // High risk files
    if (['.config', '.ini', '.cfg', '.conf'].includes(fileExt)) {
      return 'high';
    }
    if (fileName.includes('config') || fileName.includes('setting')) {
      return 'high';
    }

    // Medium risk files
    if (['.js', '.py', '.php', '.sql', '.json'].includes(fileExt)) {
      return 'medium';
    }

    // Low risk for common file types
    if (['.txt', '.log', '.tmp', '.cache'].includes(fileExt)) {
      return 'low';
    }

    return 'medium';
  }

  private getChangeReasons(filePath: string, changeType: 'added' | 'modified' | 'deleted'): string[] {
    const reasons: string[] = [];
    const fileName = path.basename(filePath).toLowerCase();
    const fileExt = path.extname(filePath).toLowerCase();
    const dirPath = path.dirname(filePath).toLowerCase();

    if (changeType === 'added') {
      reasons.push('New file detected');
      if (['.exe', '.dll'].includes(fileExt)) {
        reasons.push('Executable file added - potential security risk');
      }
    } else if (changeType === 'modified') {
      if (fileName.includes('config')) {
        reasons.push('Configuration file modified');
      }
      if (fileName.includes('log')) {
        reasons.push('Log file updated');
      }
    } else if (changeType === 'deleted') {
      reasons.push('File removed from system');
      if (['.exe', '.dll'].includes(fileExt)) {
        reasons.push('System file deleted - potential integrity issue');
      }
    }

    return reasons;
  }

  private assessRisk(changes: FileChange[]): FileIntegrityResult['riskAssessment'] {
    const criticalChanges = changes.filter(c => c.riskLevel === 'critical').length;
    const highChanges = changes.filter(c => c.riskLevel === 'high').length;

    const suspiciousPatterns: string[] = [];

    // Detect mass file operations
    if (changes.length > 50) {
      suspiciousPatterns.push('Mass file operation detected');
    }

    // Detect executable changes
    const executableChanges = changes.filter(c => 
      path.extname(c.filePath).toLowerCase() === '.exe' ||
      path.extname(c.filePath).toLowerCase() === '.dll'
    ).length;

    if (executableChanges > 0) {
      suspiciousPatterns.push(`${executableChanges} executable file(s) modified`);
    }

    // Detect system file changes
    const systemFileChanges = changes.filter(c => 
      c.filePath.toLowerCase().includes('system32') ||
      c.filePath.toLowerCase().includes('windows') ||
      c.filePath.toLowerCase().includes('/etc/') ||
      c.filePath.toLowerCase().includes('/bin/')
    ).length;

    if (systemFileChanges > 0) {
      suspiciousPatterns.push(`${systemFileChanges} system file(s) affected`);
    }

    // Determine overall risk level
    let level: 'low' | 'medium' | 'high' | 'critical';
    if (criticalChanges > 5 || systemFileChanges > 10) {
      level = 'critical';
    } else if (criticalChanges > 0 || highChanges > 10 || executableChanges > 5) {
      level = 'high';
    } else if (highChanges > 0 || changes.length > 20) {
      level = 'medium';
    } else {
      level = 'low';
    }

    return {
      level,
      criticalChanges,
      suspiciousPatterns
    };
  }
}

server/services/keylogger-service.ts.content:
import { exec } from 'child_process';
import { promisify } from 'util';

const execAsync = promisify(exec);

export interface KeyloggerDetectionResult {
  processesScanned: number;
  suspiciousProcesses: SuspiciousProcess[];
  riskLevel: 'low' | 'medium' | 'high' | 'critical';
  recommendations: string[];
  timestamp: Date;
}

export interface SuspiciousProcess {
  pid: number;
  name: string;
  command: string;
  riskScore: number;
  reasons: string[];
  user?: string;
  cpuUsage?: number;
  memoryUsage?: number;
}

const SUSPICIOUS_KEYWORDS = [
  'keylog', 'keystroke', 'capture', 'hook', 'monitor', 'record',
  'spy', 'stealth', 'hidden', 'invisible', 'backdoor', 'trojan',
  'rat', 'remote', 'access', 'screen', 'capture', 'logger',
  'dump', 'harvest', 'steal', 'inject', 'payload', 'shell',
  'reverse', 'bind', 'tunnel', 'persistence', 'privilege',
  'escalation', 'bypass', 'evasion', 'rootkit', 'malware'
];

const SUSPICIOUS_PROCESSES = [
  'keylogger', 'spyware', 'malware', 'rootkit', 'backdoor',
  'remote_access', 'rat', 'trojan', 'virus', 'worm'
];

const LEGITIMATE_PROCESSES = [
  'explorer.exe', 'chrome.exe', 'firefox.exe', 'notepad.exe',
  'code.exe', 'powershell.exe', 'cmd.exe', 'taskmgr.exe',
  'winlogon.exe', 'csrss.exe', 'lsass.exe', 'services.exe',
  'svchost.exe', 'dwm.exe', 'conhost.exe', 'RuntimeBroker.exe',
  'dllhost.exe', 'SearchIndexer.exe', 'audiodg.exe', 'wininit.exe',
  'spoolsv.exe', 'WmiPrvSE.exe', 'MsMpEng.exe', 'SecurityHealthService.exe',
  'node.exe', 'python.exe', 'java.exe', 'msedge.exe', 'opera.exe',
  'safari.exe', 'teams.exe', 'slack.exe', 'discord.exe', 'zoom.exe'
];

export class KeyloggerService {
  async detectKeyloggers(): Promise<KeyloggerDetectionResult> {
    const processes = await this.getRunningProcesses();
    const suspiciousProcesses: SuspiciousProcess[] = [];

    for (const process of processes) {
      const analysis = this.analyzeProcess(process);
      if (analysis.riskScore > 30) {
        suspiciousProcesses.push(analysis);
      }
    }

    const riskLevel = this.calculateOverallRisk(suspiciousProcesses);
    const recommendations = this.generateRecommendations(suspiciousProcesses, riskLevel);

    return {
      processesScanned: processes.length,
      suspiciousProcesses: suspiciousProcesses.sort((a, b) => b.riskScore - a.riskScore),
      riskLevel,
      recommendations,
      timestamp: new Date()
    };
  }

  private async getRunningProcesses(): Promise<any[]> {
    try {
      let command: string;
      const platform = process.platform;

      if (platform === 'win32') {
        command = 'wmic process get Name,ProcessId,CommandLine,PageFileUsage,WorkingSetSize /format:csv';
      } else if (platform === 'darwin' || platform === 'linux') {
        command = 'ps aux';
      } else {
        throw new Error('Unsupported platform for process monitoring');
      }

      const { stdout } = await execAsync(command);
      return this.parseProcessOutput(stdout, platform);
    } catch (error) {
      console.error('Error getting processes:', error);
      return [];
    }
  }

  private parseProcessOutput(output: string, platform: string): any[] {
    const processes: any[] = [];
    const lines = output.split('\n').filter(line => line.trim());

    if (platform === 'win32') {
      // Parse Windows WMIC output
      for (let i = 1; i < lines.length; i++) {
        const parts = lines[i].split(',');
        if (parts.length >= 4 && parts[1] && parts[2]) {
          processes.push({
            pid: parseInt(parts[2]) || 0,
            name: parts[1].trim(),
            command: parts[0] || '',
            memoryUsage: parseInt(parts[4]) || 0,
            user: 'unknown'
          });
        }
      }
    } else {
      // Parse Unix ps output
      for (let i = 1; i < lines.length; i++) {
        const parts = lines[i].trim().split(/\s+/);
        if (parts.length >= 11) {
          processes.push({
            pid: parseInt(parts[1]) || 0,
            name: parts[10] || 'unknown',
            command: parts.slice(10).join(' '),
            cpuUsage: parseFloat(parts[2]) || 0,
            memoryUsage: parseFloat(parts[3]) || 0,
            user: parts[0]
          });
        }
      }
    }

    return processes;
  }

  private analyzeProcess(process: any): SuspiciousProcess {
    let riskScore = 0;
    const reasons: string[] = [];

    const processName = process.name.toLowerCase();
    const command = process.command.toLowerCase();

    // Check for suspicious keywords
    for (const keyword of SUSPICIOUS_KEYWORDS) {
      if (processName.includes(keyword) || command.includes(keyword)) {
        riskScore += 25;
        reasons.push(`Contains suspicious keyword: ${keyword}`);
      }
    }

    // Check for known suspicious process names
    for (const suspiciousName of SUSPICIOUS_PROCESSES) {
      if (processName.includes(suspiciousName)) {
        riskScore += 40;
        reasons.push(`Matches known suspicious process pattern: ${suspiciousName}`);
      }
    }

    // Check for hidden or system-like names
    if (this.looksLikeSystemProcess(processName) && !this.isLegitimateSystemProcess(processName)) {
      riskScore += 20;
      reasons.push('Mimics system process name');
    }

    // Check for unusual file extensions
    if (processName.endsWith('.tmp') || processName.endsWith('.dll') || 
        processName.includes('temp') || processName.includes('cache')) {
      riskScore += 15;
      reasons.push('Unusual process location or extension');
    }

    // Check for processes without proper paths
    if (!command.includes('\\') && !command.includes('/') && command.length > 0) {
      riskScore += 10;
      reasons.push('Process running without full path');
    }

    // Check for high resource usage (potential sign of monitoring activity)
    if (process.cpuUsage > 50) {
      riskScore += 10;
      reasons.push('High CPU usage');
    }

    // Check for random-looking names
    if (this.hasRandomName(processName)) {
      riskScore += 15;
      reasons.push('Random or obfuscated process name');
    }

    return {
      pid: process.pid,
      name: process.name,
      command: process.command,
      riskScore,
      reasons,
      user: process.user,
      cpuUsage: process.cpuUsage,
      memoryUsage: process.memoryUsage
    };
  }

  private looksLikeSystemProcess(name: string): boolean {
    const systemPatterns = [
      /^sys/, /^win/, /^microsoft/, /^windows/, /^service/,
      /\.exe$/, /^lsass/, /^csrss/, /^winlogon/
    ];
    return systemPatterns.some(pattern => pattern.test(name));
  }

  private isLegitimateSystemProcess(name: string): boolean {
    return LEGITIMATE_PROCESSES.some(legit => 
      name.toLowerCase().includes(legit.toLowerCase())
    );
  }

  private hasRandomName(name: string): boolean {
    // Check for patterns that suggest random generation
    const randomPatterns = [
      /^[a-z]{8,}\.exe$/, // Long random lowercase
      /^[A-Z]{5,}\.exe$/, // Long random uppercase
      /^[a-zA-Z0-9]{12,}\.exe$/, // Very long alphanumeric
      /^[a-f0-9]{8,}\.exe$/, // Hex-like names
      /^[0-9]{6,}\.exe$/, // All numbers
      /^[a-zA-Z]{2}[0-9]{4,}\.exe$/, // Mixed patterns
      /^tmp[a-zA-Z0-9]+\.exe$/, // Temp file patterns
    ];

    // Check for entropy in the name (measure of randomness)
    const entropy = this.calculateStringEntropy(name.replace(/\.[^.]*$/, ''));

    return randomPatterns.some(pattern => pattern.test(name)) || entropy > 4.2;
  }

  private calculateStringEntropy(str: string): number {
    const freq: { [key: string]: number } = {};
    for (const char of str) {
      freq[char] = (freq[char] || 0) + 1;
    }

    let entropy = 0;
    const len = str.length;
    for (const count of Object.values(freq)) {
      const p = count / len;
      entropy -= p * Math.log2(p);
    }

    return entropy;
  }

  private calculateOverallRisk(suspiciousProcesses: SuspiciousProcess[]): KeyloggerDetectionResult['riskLevel'] {
    if (suspiciousProcesses.length === 0) return 'low';

    const maxRiskScore = Math.max(...suspiciousProcesses.map(p => p.riskScore));
    const totalRiskScore = suspiciousProcesses.reduce((sum, p) => sum + p.riskScore, 0);

    if (maxRiskScore >= 80 || totalRiskScore >= 150) return 'critical';
    if (maxRiskScore >= 60 || totalRiskScore >= 100) return 'high';
    if (maxRiskScore >= 40 || totalRiskScore >= 60) return 'medium';
    return 'low';
  }

  private generateRecommendations(
    suspiciousProcesses: SuspiciousProcess[], 
    riskLevel: KeyloggerDetectionResult['riskLevel']
  ): string[] {
    const recommendations: string[] = [];

    if (riskLevel === 'critical') {
      recommendations.push('URGENT: Immediately terminate suspicious processes');
      recommendations.push('Disconnect from network to prevent data exfiltration');
      recommendations.push('Run full system antivirus scan');
      recommendations.push('Contact your security team immediately');
    }

    if (riskLevel === 'high') {
      recommendations.push('Investigate suspicious processes immediately');
      recommendations.push('Consider isolating the system');
      recommendations.push('Run comprehensive malware scan');
    }

    if (suspiciousProcesses.length > 0) {
      recommendations.push('Monitor process activity closely');
      recommendations.push('Check process digital signatures');
      recommendations.push('Verify process locations and origins');
    }

    recommendations.push('Keep antivirus software updated');
    recommendations.push('Enable real-time protection');
    recommendations.push('Regularly monitor running processes');
    recommendations.push('Use application whitelisting if possible');

    return recommendations;
  }

  async terminateProcess(pid: number): Promise<boolean> {
    try {
      const command = process.platform === 'win32' 
        ? `taskkill /F /PID ${pid}`
        : `kill -9 ${pid}`;

      await execAsync(command);
      return true;
    } catch (error) {
      console.error('Error terminating process:', error);
      return false;
    }
  }
}

server/services/password-service.ts.content:
import crypto from 'crypto';

export interface PasswordAnalysis {
  score: number;
  strength: 'very-weak' | 'weak' | 'medium' | 'strong' | 'very-strong';
  criteria: {
    length: boolean;
    specialChars: boolean;
    numbers: boolean;
    upperCase: boolean;
    lowerCase: boolean;
    noDictionaryWords: boolean;
  };
  entropy: number;
  suggestions: string[];
  crackTime: string;
}

const COMMON_PASSWORDS = [
  'password', '123456', '123456789', 'qwerty', 'abc123', 'monkey', 
  'letmein', 'dragon', '111111', 'baseball', 'iloveyou', 'trustno1',
  'sunshine', 'master', 'welcome', 'shadow', 'ashley', 'football',
  'jesus', 'michael', 'ninja', 'mustang', 'password1', 'admin',
  'password123', '12345678', 'qwerty123', 'password!', 'welcome123',
  'password2024', '123qwe', 'qwertyuiop', 'zxcvbnm', 'asdfghjkl',
  'passw0rd', 'p@ssword', 'secret', 'login', 'root', 'toor',
  'guest', 'user', 'test', 'demo', 'temp', 'changeme', 'default'
];

const DICTIONARY_WORDS = [
  'love', 'hate', 'life', 'death', 'fire', 'water', 'earth', 'wind',
  'happy', 'sad', 'angry', 'peace', 'war', 'light', 'dark', 'good',
  'evil', 'fast', 'slow', 'big', 'small', 'hot', 'cold', 'sweet',
  'bitter', 'strong', 'weak', 'rich', 'poor', 'young', 'old'
];

export class PasswordService {
  analyzePassword(password: string): PasswordAnalysis {
    const criteria = this.checkCriteria(password);
    const entropy = this.calculateEntropy(password);
    const score = this.calculateScore(password, criteria, entropy);
    const strength = this.getStrengthLevel(score);
    const suggestions = this.generateSuggestions(password, criteria);
    const crackTime = this.estimateCrackTime(entropy);

    return {
      score,
      strength,
      criteria,
      entropy,
      suggestions,
      crackTime
    };
  }

  private checkCriteria(password: string) {
    return {
      length: password.length >= 8,
      specialChars: /[!@#$%^&*(),.?":{}|<>]/.test(password),
      numbers: /\d/.test(password),
      upperCase: /[A-Z]/.test(password),
      lowerCase: /[a-z]/.test(password),
      noDictionaryWords: !this.containsDictionaryWords(password.toLowerCase())
    };
  }

  private containsDictionaryWords(password: string): boolean {
    const lowerPassword = password.toLowerCase();
    return COMMON_PASSWORDS.some(common => lowerPassword.includes(common)) ||
           DICTIONARY_WORDS.some(word => lowerPassword.includes(word));
  }

  private calculateEntropy(password: string): number {
    let charsetSize = 0;

    if (/[a-z]/.test(password)) charsetSize += 26;
    if (/[A-Z]/.test(password)) charsetSize += 26;
    if (/\d/.test(password)) charsetSize += 10;
    if (/[!@#$%^&*(),.?":{}|<>]/.test(password)) charsetSize += 32;

    return Math.log2(Math.pow(charsetSize, password.length));
  }

  private calculateScore(password: string, criteria: any, entropy: number): number {
    let score = 0;

    // Length scoring
    if (password.length >= 12) score += 25;
    else if (password.length >= 8) score += 15;
    else if (password.length >= 6) score += 10;

    // Character variety scoring
    if (criteria.upperCase) score += 10;
    if (criteria.lowerCase) score += 10;
    if (criteria.numbers) score += 10;
    if (criteria.specialChars) score += 15;

    // Dictionary words penalty
    if (!criteria.noDictionaryWords) score -= 20;

    // Entropy bonus
    if (entropy >= 60) score += 20;
    else if (entropy >= 50) score += 15;
    else if (entropy >= 40) score += 10;

    // Repetition penalty
    if (this.hasRepeatingPatterns(password)) score -= 10;

    return Math.max(0, Math.min(100, score));
  }

  private hasRepeatingPatterns(password: string): boolean {
    // Check for repeated characters (3 or more)
    const repeatedChars = /(.)\1{2,}/.test(password);

    // Check for sequential patterns (numbers and letters)
    const sequential = /(012|123|234|345|456|567|678|789|890|901|abc|bcd|cde|def|efg|fgh|ghi|hij|ijk|jkl|klm|lmn|mno|nop|opq|pqr|qrs|rst|stu|tuv|uvw|vwx|wxy|xyz|zyx|yxw|xwv|wvu|vut|uts|tsr|srq|rqp|qpo|pon|onm|nml|mlk|lkj|kji|jih|ihg|hgf|gfe|fed|edc|dcb|cba)/i.test(password);

    // Check for keyboard patterns
    const keyboardPatterns = /(qwer|wert|erty|rtyu|tyui|yuio|uiop|asdf|sdfg|dfgh|fghj|ghjk|hjkl|zxcv|xcvb|cvbn|vbnm|qaz|wsx|edc|rfv|tgb|yhn|ujm|1qaz|2wsx|3edc|4rfv|5tgb|6yhn|7ujm|8ik|9ol|0p)/i.test(password);

    // Check for alternating patterns
    const alternating = /^(.)(.)\1\2/.test(password) || /^(.)(.)(.)\1\2\3/.test(password);

    return repeatedChars || sequential || keyboardPatterns || alternating;
  }

  private getStrengthLevel(score: number): PasswordAnalysis['strength'] {
    if (score >= 80) return 'very-strong';
    if (score >= 60) return 'strong';
    if (score >= 40) return 'medium';
    if (score >= 20) return 'weak';
    return 'very-weak';
  }

  private generateSuggestions(password: string, criteria: any): string[] {
    const suggestions: string[] = [];

    if (!criteria.length) {
      suggestions.push('Use at least 8 characters (12+ recommended)');
    }
    if (!criteria.upperCase) {
      suggestions.push('Add uppercase letters (A-Z)');
    }
    if (!criteria.lowerCase) {
      suggestions.push('Add lowercase letters (a-z)');
    }
    if (!criteria.numbers) {
      suggestions.push('Include numbers (0-9)');
    }
    if (!criteria.specialChars) {
      suggestions.push('Add special characters (!@#$%^&*)');
    }
    if (!criteria.noDictionaryWords) {
      suggestions.push('Avoid common words and phrases');
    }
    if (password.length < 12) {
      suggestions.push('Consider using a longer passphrase');
    }
    if (this.hasRepeatingPatterns(password)) {
      suggestions.push('Avoid repetitive patterns and sequences');
    }

    return suggestions;
  }

  private estimateCrackTime(entropy: number): string {
    const attempts = Math.pow(2, entropy) / 2; // Average case

    // Modern attack scenarios
    const scenarios = {
      basic: 1e3,        // Basic online attack
      moderate: 1e6,     // Offline attack with consumer hardware
      advanced: 1e12,    // Dedicated cracking rig
      quantum: 1e15      // Future quantum computing threat
    };

    const scenario = scenarios.advanced; // Use realistic threat model
    const seconds = attempts / scenario;

    if (seconds < 1) return 'Instantly';
    if (seconds < 60) return `${Math.ceil(seconds)} seconds`;
    if (seconds < 3600) return `${Math.ceil(seconds / 60)} minutes`;
    if (seconds < 86400) return `${Math.ceil(seconds / 3600)} hours`;
    if (seconds < 2592000) return `${Math.ceil(seconds / 86400)} days`;
    if (seconds < 31536000) return `${Math.ceil(seconds / 2592000)} months`;
    if (seconds < 3153600000) return `${Math.ceil(seconds / 31536000)} years`;

    return 'Centuries';
  }
}

server/services/phishing-service.ts.content:
import { URL } from 'url';

export interface PhishingAnalysis {
  score: number;
  risk: 'low' | 'medium' | 'high' | 'critical';
  indicators: {
    ipBasedUrl: boolean;
    suspiciousSubdomains: boolean;
    shortUrl: boolean;
    suspiciousKeywords: boolean;
    missingHttps: boolean;
    domainAge: 'new' | 'medium' | 'established' | 'unknown';
  };
  details: string[];
  recommendations: string[];
}

const SUSPICIOUS_KEYWORDS = [
  'paypal', 'amazon', 'netflix', 'microsoft', 'google', 'apple', 'facebook',
  'secure', 'verify', 'update', 'suspended', 'confirm', 'urgent', 'click',
  'winner', 'congratulations', 'prize', 'offer', 'limited', 'act-now',
  'banking', 'account', 'login', 'signin', 'verification', 'auth', 'security',
  'alert', 'warning', 'notice', 'expired', 'renewal', 'billing', 'payment',
  'refund', 'claim', 'validate', 'activate', 'unlock', 'restore', 'recover',
  'support', 'service', 'helpdesk', 'customer', 'team', 'department',
  'cryptocurrency', 'bitcoin', 'ethereum', 'wallet', 'trading', 'investment'
];

const SHORT_URL_DOMAINS = [
  'bit.ly', 'tinyurl.com', 't.co', 'goo.gl', 'ow.ly', 'is.gd', 
  'buff.ly', 's.id', 'v.gd', 'x.co', 'short.link'
];

const LEGITIMATE_DOMAINS = [
  'google.com', 'youtube.com', 'facebook.com', 'amazon.com', 'microsoft.com',
  'apple.com', 'twitter.com', 'instagram.com', 'linkedin.com', 'github.com',
  'stackoverflow.com', 'reddit.com', 'wikipedia.org', 'paypal.com', 'ebay.com'
];

export class PhishingService {
  analyzeUrl(urlString: string): PhishingAnalysis {
    try {
      const url = new URL(urlString);
      const indicators = this.checkIndicators(url);
      const score = this.calculateRiskScore(indicators, url);
      const risk = this.getRiskLevel(score);
      const details = this.generateDetails(indicators, url);
      const recommendations = this.generateRecommendations(indicators, risk);

      return {
        score,
        risk,
        indicators,
        details,
        recommendations
      };
    } catch (error) {
      return {
        score: 100,
        risk: 'critical',
        indicators: {
          ipBasedUrl: false,
          suspiciousSubdomains: false,
          shortUrl: false,
          suspiciousKeywords: false,
          missingHttps: true,
          domainAge: 'unknown'
        },
        details: ['Invalid URL format'],
        recommendations: ['Verify the URL format and try again']
      };
    }
  }

  private checkIndicators(url: URL) {
    return {
      ipBasedUrl: this.isIpBasedUrl(url.hostname),
      suspiciousSubdomains: this.hasSuspiciousSubdomains(url.hostname),
      shortUrl: this.isShortUrl(url.hostname),
      suspiciousKeywords: this.containsSuspiciousKeywords(url.href),
      missingHttps: url.protocol !== 'https:',
      domainAge: this.estimateDomainAge(url.hostname)
    };
  }

  private isIpBasedUrl(hostname: string): boolean {
    const ipv4Regex = /^(?:[0-9]{1,3}\.){3}[0-9]{1,3}$/;
    const ipv6Regex = /^(?:[0-9a-fA-F]{1,4}:){7}[0-9a-fA-F]{1,4}$/;
    return ipv4Regex.test(hostname) || ipv6Regex.test(hostname);
  }

  private hasSuspiciousSubdomains(hostname: string): boolean {
    const parts = hostname.split('.');

    // More than 3 subdomains is suspicious
    if (parts.length > 4) return true;

    // Check for suspicious patterns
    const suspiciousPatterns = [
      /\d{4,}/, // Long numbers
      /[a-z]{15,}/, // Very long random strings
      /-{2,}/, // Multiple hyphens
      /secure.*secure/, // Repeated "secure"
      /verify.*verify/, // Repeated "verify"
    ];

    return parts.some(part => 
      suspiciousPatterns.some(pattern => pattern.test(part))
    );
  }

  private isShortUrl(hostname: string): boolean {
    return SHORT_URL_DOMAINS.includes(hostname.toLowerCase());
  }

  private containsSuspiciousKeywords(url: string): boolean {
    const lowerUrl = url.toLowerCase();
    return SUSPICIOUS_KEYWORDS.some(keyword => lowerUrl.includes(keyword));
  }

  private estimateDomainAge(hostname: string): PhishingAnalysis['indicators']['domainAge'] {
    // Simplified domain age estimation based on known domains
    if (LEGITIMATE_DOMAINS.includes(hostname.toLowerCase())) {
      return 'established';
    }

    // Very simple heuristic - in real implementation, you'd query WHOIS data
    const tld = hostname.split('.').pop()?.toLowerCase();
    if (tld === 'tk' || tld === 'ml' || tld === 'ga' || tld === 'cf') {
      return 'new'; // Free TLDs often used by malicious sites
    }

    return 'unknown';
  }

  private calculateRiskScore(indicators: PhishingAnalysis['indicators'], url: URL): number {
    let score = 0;

    if (indicators.ipBasedUrl) score += 30;
    if (indicators.suspiciousSubdomains) score += 25;
    if (indicators.shortUrl) score += 20;
    if (indicators.suspiciousKeywords) score += 20;
    if (indicators.missingHttps) score += 15;

    switch (indicators.domainAge) {
      case 'new': score += 25; break;
      case 'medium': score += 10; break;
      case 'established': score -= 20; break;
    }

    // Additional checks
    if (this.hasHomographAttack(url.hostname)) score += 30;
    if (this.hasExcessiveRedirects(url.href)) score += 15;
    if (this.hasSuspiciousPort(url.port)) score += 20;

    return Math.max(0, Math.min(100, score));
  }

  private hasHomographAttack(hostname: string): boolean {
    // Check for mixed scripts or confusing characters
    const cyrillicPattern = /[Ð°-Ñ]/i; // Cyrillic letters
    const greekPattern = /[Î±-Ï‰]/i; // Greek letters  
    const fullwidthPattern = /[ï½-ï½š]/i; // Fullwidth Latin

    // Check for confusing character combinations in domain
    const confusingChars = /[0oOQ1lI5sS2zZ6G9gq]/;
    const hasConfusing = confusingChars.test(hostname) && hostname.length > 3;

    // Check for IDN (Internationalized Domain Name) encoding
    const idnPattern = /xn--/;

    // Look for suspicious Unicode substitutions
    const suspiciousUnicode = cyrillicPattern.test(hostname) || 
                             greekPattern.test(hostname) || 
                             fullwidthPattern.test(hostname);

    return suspiciousUnicode || (hasConfusing && idnPattern.test(hostname));
  }

  private hasExcessiveRedirects(url: string): boolean {
    // Simple check for multiple redirects in URL
    return (url.match(/http/g) || []).length > 1;
  }

  private hasSuspiciousPort(port: string): boolean {
    if (!port) return false;
    const portNum = parseInt(port);
    // Common non-standard ports that could indicate suspicious activity
    const suspiciousPorts = [8080, 8000, 3000, 5000, 9000, 8443, 8888, 9999, 4444, 31337, 1337, 8008];
    // Very high ports that might be used to avoid detection
    const highPort = portNum > 49152;
    return suspiciousPorts.includes(portNum) || highPort;
  }

  private getRiskLevel(score: number): PhishingAnalysis['risk'] {
    if (score >= 70) return 'critical';
    if (score >= 50) return 'high';
    if (score >= 30) return 'medium';
    return 'low';
  }

  private generateDetails(indicators: PhishingAnalysis['indicators'], url: URL): string[] {
    const details: string[] = [];

    if (indicators.ipBasedUrl) {
      details.push('URL uses IP address instead of domain name');
    }
    if (indicators.suspiciousSubdomains) {
      details.push('Domain has suspicious subdomain patterns');
    }
    if (indicators.shortUrl) {
      details.push('URL uses a URL shortening service');
    }
    if (indicators.suspiciousKeywords) {
      details.push('URL contains suspicious keywords often used in phishing');
    }
    if (indicators.missingHttps) {
      details.push('Connection is not encrypted (HTTP instead of HTTPS)');
    }

    switch (indicators.domainAge) {
      case 'new':
        details.push('Domain appears to be newly registered');
        break;
      case 'established':
        details.push('Domain has an established online presence');
        break;
      case 'unknown':
        details.push('Unable to determine domain age');
        break;
    }

    if (details.length === 0) {
      details.push('No obvious phishing indicators detected');
    }

    return details;
  }

  private generateRecommendations(indicators: PhishingAnalysis['indicators'], risk: PhishingAnalysis['risk']): string[] {
    const recommendations: string[] = [];

    if (risk === 'critical' || risk === 'high') {
      recommendations.push('DO NOT enter personal information on this site');
      recommendations.push('Verify the legitimate website URL directly');
      recommendations.push('Report this URL to your security team');
    }

    if (indicators.missingHttps) {
      recommendations.push('Look for HTTPS encryption before entering sensitive data');
    }

    if (indicators.shortUrl) {
      recommendations.push('Expand shortened URLs to see the actual destination');
    }

    if (indicators.suspiciousKeywords) {
      recommendations.push('Be cautious of urgent language and requests for immediate action');
    }

    if (risk === 'low') {
      recommendations.push('Always verify URLs match the official website');
      recommendations.push('Keep your browser and security software updated');
    }

    return recommendations;
  }
}

server/services/port-service.ts.content:
import { createConnection, Socket } from 'net';

export interface PortScanResult {
  target: string;
  totalPorts: number;
  openPorts: OpenPort[];
  scanDuration: number;
  timestamp: Date;
}

export interface OpenPort {
  port: number;
  state: 'open' | 'closed' | 'filtered';
  service: string;
  version?: string;
  banner?: string;
}

const COMMON_SERVICES: { [key: number]: string } = {
  21: 'FTP',
  22: 'SSH',
  23: 'Telnet',
  25: 'SMTP',
  53: 'DNS',
  80: 'HTTP',
  110: 'POP3',
  135: 'RPC Endpoint Mapper',
  139: 'NetBIOS',
  143: 'IMAP',
  161: 'SNMP',
  389: 'LDAP',
  443: 'HTTPS',
  445: 'SMB',
  993: 'IMAPS',
  995: 'POP3S',
  1433: 'MSSQL',
  1521: 'Oracle',
  2049: 'NFS',
  3306: 'MySQL',
  3389: 'RDP',
  5432: 'PostgreSQL',
  5800: 'VNC HTTP',
  5900: 'VNC',
  6379: 'Redis',
  8080: 'HTTP-Alt',
  8443: 'HTTPS-Alt',
  9200: 'Elasticsearch',
  11211: 'Memcached',
  27017: 'MongoDB',
  50070: 'Hadoop'
};

export class PortService {
  async scanPorts(target: string, portRange: string = '1-1000'): Promise<PortScanResult> {
    const startTime = Date.now();
    const ports = this.parsePortRange(portRange);
    const openPorts: OpenPort[] = [];

    // Validate target
    if (!this.isValidTarget(target)) {
      throw new Error('Invalid target address');
    }

    // Security check for localhost/private ranges
    if (this.isPrivateOrLocalhost(target)) {
      console.warn(`Scanning private/localhost target: ${target}`);
    }

    // Adaptive concurrency based on port count
    const concurrencyLimit = Math.min(50, Math.max(10, Math.floor(ports.length / 10)));
    const chunks = this.chunkArray(ports, concurrencyLimit);

    for (const chunk of chunks) {
      const promises = chunk.map(port => this.scanPort(target, port));
      const results = await Promise.allSettled(promises);

      results.forEach((result, index) => {
        if (result.status === 'fulfilled' && result.value) {
          openPorts.push(result.value);
        }
      });

      // Add small delay between chunks to be respectful
      if (chunks.indexOf(chunk) < chunks.length - 1) {
        await new Promise(resolve => setTimeout(resolve, 100));
      }
    }

    // Enhanced analysis of open ports
    this.analyzePortSecurity(openPorts);

    const scanDuration = Date.now() - startTime;

    return {
      target,
      totalPorts: ports.length,
      openPorts: openPorts.sort((a, b) => a.port - b.port),
      scanDuration,
      timestamp: new Date()
    };
  }

  private parsePortRange(portRange: string): number[] {
    const ports: number[] = [];

    if (portRange.includes(',')) {
      // Handle comma-separated ports: "80,443,8080"
      const portList = portRange.split(',');
      for (const port of portList) {
        const portNum = parseInt(port.trim());
        if (portNum >= 1 && portNum <= 65535) {
          ports.push(portNum);
        }
      }
    } else if (portRange.includes('-')) {
      // Handle port ranges: "1-1000"
      const [start, end] = portRange.split('-');
      const startPort = parseInt(start.trim());
      const endPort = parseInt(end.trim());

      if (startPort >= 1 && endPort <= 65535 && startPort <= endPort) {
        for (let port = startPort; port <= endPort; port++) {
          ports.push(port);
        }
      }
    } else {
      // Single port
      const portNum = parseInt(portRange.trim());
      if (portNum >= 1 && portNum <= 65535) {
        ports.push(portNum);
      }
    }

    return ports;
  }

  private isValidTarget(target: string): boolean {
    // Basic validation for IP addresses and hostnames
    const ipv4Regex = /^(?:[0-9]{1,3}\.){3}[0-9]{1,3}$/;
    const hostnameRegex = /^[a-zA-Z0-9.-]+$/;

    return ipv4Regex.test(target) || hostnameRegex.test(target);
  }

  private chunkArray<T>(array: T[], size: number): T[][] {
    const chunks: T[][] = [];
    for (let i = 0; i < array.length; i += size) {
      chunks.push(array.slice(i, i + size));
    }
    return chunks;
  }

  private async scanPort(target: string, port: number): Promise<OpenPort | null> {
    return new Promise((resolve) => {
      const socket = new Socket();
      const timeout = 2000; // 2 second timeout

      socket.setTimeout(timeout);

      socket.on('connect', async () => {
        socket.destroy();

        // Try to get banner information
        const banner = await this.getBanner(target, port);

        resolve({
          port,
          state: 'open',
          service: COMMON_SERVICES[port] || 'Unknown',
          banner
        });
      });

      socket.on('timeout', () => {
        socket.destroy();
        resolve(null);
      });

      socket.on('error', () => {
        socket.destroy();
        resolve(null);
      });

      socket.connect(port, target);
    });
  }

  private async getBanner(target: string, port: number): Promise<string | undefined> {
    return new Promise((resolve) => {
      const socket = new Socket();
      let banner = '';

      socket.setTimeout(3000);

      socket.on('connect', () => {
        // Send appropriate probe based on service
        const probe = this.getServiceProbe(port);
        if (probe) {
          socket.write(probe);
        }
      });

      socket.on('data', (data) => {
        banner += data.toString().trim();
        socket.destroy();
        resolve(banner || undefined);
      });

      socket.on('timeout', () => {
        socket.destroy();
        resolve(banner || undefined);
      });

      socket.on('error', () => {
        socket.destroy();
        resolve(undefined);
      });

      socket.connect(port, target);
    });
  }

  private getServiceProbe(port: number): string | null {
    switch (port) {
      case 21: // FTP
        return 'HELP\r\n';
      case 22: // SSH
        return 'SSH-2.0-Scanner\r\n';
      case 25: // SMTP
        return 'EHLO scanner\r\n';
      case 80: // HTTP
      case 8080:
        return 'GET / HTTP/1.0\r\n\r\n';
      case 443: // HTTPS
      case 8443:
        return 'GET / HTTP/1.0\r\n\r\n';
      case 110: // POP3
        return 'USER scanner\r\n';
      case 143: // IMAP
        return 'A001 CAPABILITY\r\n';
      default:
        return null;
    }
  }

  getCommonPorts(): number[] {
    return [21, 22, 23, 25, 53, 80, 110, 135, 139, 143, 161, 389, 443, 445, 993, 995, 1433, 1521, 2049, 3306, 3389, 5432, 5800, 5900, 6379, 8080, 8443, 9200, 11211, 27017, 50070];
  }

  private isPrivateOrLocalhost(target: string): boolean {
    const ipv4Regex = /^(?:[0-9]{1,3}\.){3}[0-9]{1,3}$/;
    if (ipv4Regex.test(target)) {
      const parts = target.split('.').map(Number);
      // Check for private IP ranges
      return (parts[0] === 10) ||
             (parts[0] === 172 && parts[1] >= 16 && parts[1] <= 31) ||
             (parts[0] === 192 && parts[1] === 168) ||
             (parts[0] === 127) || // Localhost
             (parts[0] === 169 && parts[1] === 254); // Link-local
    }
    return target === 'localhost' || target.endsWith('.local');
  }

  private analyzePortSecurity(openPorts: OpenPort[]): void {
    openPorts.forEach(port => {
      // Mark potentially dangerous services
      const dangerousPorts = [21, 23, 135, 139, 445, 1433, 3306, 3389, 5432, 5900];
      if (dangerousPorts.includes(port.port)) {
        port.version = (port.version || '') + ' [HIGH RISK]';
      }

      // Add security notes for specific services
      if (port.port === 22 && port.banner?.includes('SSH-1')) {
        port.version = (port.version || '') + ' [VULNERABLE SSH VERSION]';
      }
      if (port.port === 21 && port.banner?.toLowerCase().includes('anonymous')) {
        port.version = (port.version || '') + ' [ANONYMOUS FTP]';
      }
    });
  }

  async quickScan(target: string): Promise<PortScanResult> {
    const commonPorts = this.getCommonPorts();
    return this.scanPorts(target, commonPorts.join(','));
  }
}

