import { useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import { Card, CardContent } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Fish, Search, Check, X, Info, AlertTriangle } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface PhishingAnalysis {
  score: number;
  risk: 'low' | 'medium' | 'high' | 'critical';
  indicators: {
    ipBasedUrl: boolean;
    suspiciousSubdomains: boolean;
    shortUrl: boolean;
    suspiciousKeywords: boolean;
    missingHttps: boolean;
    domainAge: 'new' | 'medium' | 'established' | 'unknown';
  };
  details: string[];
  recommendations: string[];
}

export function PhishingDetector() {
  const [url, setUrl] = useState('');
  const [analysis, setAnalysis] = useState<PhishingAnalysis | null>(null);
  const { toast } = useToast();

  const analyzeUrlMutation = useMutation({
    mutationFn: async (url: string) => {
      const response = await apiRequest('POST', '/api/security/phishing-analysis', { url });
      return response.json();
    },
    onSuccess: (data) => {
      setAnalysis(data);
    },
    onError: (error: any) => {
      // Extract clean error message
      let errorMessage = "Failed to analyze URL";
      if (error?.message) {
        errorMessage = error.message.replace(/^\d+:\s*/, ''); // Remove status codes
      }
      toast({
        title: "Analysis Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const handleAnalyze = () => {
    if (url.trim()) {
      analyzeUrlMutation.mutate(url.trim());
    }
  };

  const getRiskColor = (risk: string) => {
    switch (risk) {
      case 'low': return 'bg-accent/20 text-accent';
      case 'medium': return 'bg-yellow-500/20 text-yellow-500';
      case 'high': return 'bg-orange-500/20 text-orange-500';
      case 'critical': return 'bg-destructive/20 text-destructive';
      default: return 'bg-muted/20 text-muted-foreground';
    }
  };

  const getRiskIcon = (risk: string) => {
    switch (risk) {
      case 'low': return <Check className="h-4 w-4" />;
      case 'medium': return <Info className="h-4 w-4" />;
      case 'high': 
      case 'critical': return <AlertTriangle className="h-4 w-4" />;
      default: return <Info className="h-4 w-4" />;
    }
  };

  return (
    <Card className="bg-card border border-border">
      <CardContent className="p-4 sm:p-6">
        <div className="flex items-center space-x-3 mb-4 sm:mb-6">
          <div className="w-10 h-10 sm:w-12 sm:h-12 bg-destructive/10 rounded-lg flex items-center justify-center">
            <Fish className="text-destructive text-lg sm:text-xl" />
          </div>
          <div className="min-w-0 flex-1">
            <h3 className="text-base sm:text-lg font-semibold">Phishing URL Detection</h3>
            <p className="text-xs sm:text-sm text-muted-foreground">Detect malicious URLs and phishing attempts</p>
          </div>
        </div>

        <div className="space-y-4">
          <div>
            <Label htmlFor="url-input" className="block text-sm font-medium mb-2">
              URL to Analyze
            </Label>
            <Input
              id="url-input"
              type="url"
              placeholder="https://example.com"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              className="w-full font-mono"
              data-testid="input-url"
            />
          </div>

          <Button 
            onClick={handleAnalyze}
            disabled={!url.trim() || analyzeUrlMutation.isPending}
            className="w-full"
            data-testid="button-analyze-url"
          >
            <Search className="mr-2 h-4 w-4" />
            <span className="hidden sm:inline">{analyzeUrlMutation.isPending ? "Analyzing..." : "Analyze URL"}</span>
            <span className="sm:hidden">{analyzeUrlMutation.isPending ? "Analyzing..." : "Analyze"}</span>
          </Button>

          {analysis && (
            <>
              {/* Analysis Results */}
              <div className="bg-secondary p-3 sm:p-4 rounded-lg">
                <div className="flex items-center justify-between mb-3">
                  <h4 className="font-medium">Security Assessment</h4>
                  <Badge className={`${getRiskColor(analysis.risk)} flex items-center space-x-1`}>
                    {getRiskIcon(analysis.risk)}
                    <span>{analysis.risk.charAt(0).toUpperCase() + analysis.risk.slice(1)} Risk</span>
                  </Badge>
                </div>

                <div className="space-y-3 text-sm">
                  <div className="flex items-center justify-between">
                    <span>Risk Score</span>
                    <span className="font-mono" data-testid="text-risk-score">{analysis.score}/100</span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span>IP-based URL</span>
                    {analysis.indicators.ipBasedUrl ? (
                      <X className="h-4 w-4 text-destructive" />
                    ) : (
                      <Check className="h-4 w-4 text-accent" />
                    )}
                  </div>
                  <div className="flex items-center justify-between">
                    <span>SSL Certificate</span>
                    {!analysis.indicators.missingHttps ? (
                      <Check className="h-4 w-4 text-accent" />
                    ) : (
                      <X className="h-4 w-4 text-destructive" />
                    )}
                  </div>
                  <div className="flex items-center justify-between">
                    <span>Domain Age</span>
                    <span className={analysis.indicators.domainAge === 'established' ? 'text-accent' : 'text-muted-foreground'}>
                      {analysis.indicators.domainAge}
                    </span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span>Suspicious Keywords</span>
                    {!analysis.indicators.suspiciousKeywords ? (
                      <Check className="h-4 w-4 text-accent" />
                    ) : (
                      <X className="h-4 w-4 text-destructive" />
                    )}
                  </div>
                  <div className="flex items-center justify-between">
                    <span>Short URL</span>
                    {!analysis.indicators.shortUrl ? (
                      <Check className="h-4 w-4 text-accent" />
                    ) : (
                      <X className="h-4 w-4 text-destructive" />
                    )}
                  </div>
                </div>
              </div>

              {/* Detailed Analysis */}
              <div className="bg-muted p-3 sm:p-4 rounded-lg">
                <h4 className="font-medium mb-2 flex items-center">
                  <Info className="mr-2 h-4 w-4" />
                  Analysis Details
                </h4>
                <div className="text-sm text-muted-foreground space-y-1">
                  {analysis.details.map((detail, index) => (
                    <p key={index}>â€¢ {detail}</p>
                  ))}
                </div>
              </div>

              {/* Recommendations */}
              {analysis.recommendations.length > 0 && (
                <div className="bg-muted p-3 sm:p-4 rounded-lg">
                  <h4 className="font-medium mb-2 flex items-center">
                    <AlertTriangle className="mr-2 h-4 w-4" />
                    Recommendations
                  </h4>
                  <div className="text-sm text-muted-foreground space-y-1">
                    {analysis.recommendations.map((recommendation, index) => (
                      <p key={index}>â€¢ {recommendation}</p>
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/security/port-scan.content:
import { useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import { Card, CardContent } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Network, Play, Zap } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface OpenPort {
  port: number;
  state: 'open' | 'closed' | 'filtered';
  service: string;
  version?: string;
  banner?: string;
}

interface PortScanResult {
  target: string;
  totalPorts: number;
  openPorts: OpenPort[];
  scanDuration: number;
  timestamp: string;
}

export function PortScanner() {
  const [target, setTarget] = useState('');
  const [portRange, setPortRange] = useState('1-1000');
  const [scanResult, setScanResult] = useState<PortScanResult | null>(null);
  const { toast } = useToast();

  const scanPortsMutation = useMutation({
    mutationFn: async ({ target, portRange }: { target: string; portRange: string }) => {
      const response = await apiRequest('POST', '/api/security/port-scan', { target, portRange });
      return response.json();
    },
    onSuccess: (data) => {
      setScanResult(data);
      toast({
        title: "Scan Complete",
        description: `Found ${data.openPorts.length} open ports on ${data.target}`,
      });
    },
    onError: (error: any) => {
      // Extract clean error message
      let errorMessage = "Failed to scan ports";
      if (error?.message) {
        errorMessage = error.message.replace(/^\d+:\s*/, ''); // Remove status codes
      }
      toast({
        title: "Scan Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const quickScanMutation = useMutation({
    mutationFn: async (target: string) => {
      const response = await apiRequest('POST', '/api/security/port-scan-quick', { target });
      return response.json();
    },
    onSuccess: (data) => {
      setScanResult(data);
      toast({
        title: "Quick Scan Complete",
        description: `Found ${data.openPorts.length} open ports on ${data.target}`,
      });
    },
    onError: (error: any) => {
      // Extract clean error message
      let errorMessage = "Failed to perform quick scan";
      if (error?.message) {
        errorMessage = error.message.replace(/^\d+:\s*/, ''); // Remove status codes
      }
      toast({
        title: "Quick Scan Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const handleFullScan = () => {
    if (target.trim()) {
      scanPortsMutation.mutate({ target: target.trim(), portRange });
    }
  };

  const handleQuickScan = () => {
    if (target.trim()) {
      quickScanMutation.mutate(target.trim());
    }
  };

  const isScanning = scanPortsMutation.isPending || quickScanMutation.isPending;

  return (
    <Card className="bg-card border border-border">
      <CardContent className="p-4 sm:p-6">
        <div className="flex items-center space-x-3 mb-4 sm:mb-6">
          <div className="w-10 h-10 sm:w-12 sm:h-12 bg-chart-3/10 rounded-lg flex items-center justify-center">
            <Network className="text-chart-3 text-lg sm:text-xl" />
          </div>
          <div className="min-w-0 flex-1">
            <h3 className="text-base sm:text-lg font-semibold">Port Scanner</h3>
            <p className="text-xs sm:text-sm text-muted-foreground">Scan network ports and identify running services</p>
          </div>
        </div>

        <div className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="target-input" className="block text-sm font-medium mb-2">
                Target IP/Domain
              </Label>
              <Input
                id="target-input"
                type="text"
                placeholder="192.168.1.1 or example.com"
                value={target}
                onChange={(e) => setTarget(e.target.value)}
                className="w-full font-mono text-sm"
                data-testid="input-target"
              />
            </div>
            <div>
              <Label htmlFor="port-range-input" className="block text-sm font-medium mb-2">
                Port Range
              </Label>
              <Input
                id="port-range-input"
                type="text"
                placeholder="1-1000 or 80,443,8080"
                value={portRange}
                onChange={(e) => setPortRange(e.target.value)}
                className="w-full font-mono text-sm"
                data-testid="input-port-range"
              />
            </div>
          </div>

          <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-2">
            <Button
              onClick={handleFullScan}
              disabled={!target.trim() || isScanning}
              className="flex-1"
              data-testid="button-full-scan"
            >
              <Play className="mr-2 h-4 w-4" />
              <span className="hidden sm:inline">{scanPortsMutation.isPending ? "Scanning..." : "Start Scan"}</span>
              <span className="sm:hidden">{scanPortsMutation.isPending ? "Scanning..." : "Scan"}</span>
            </Button>
            <Button
              variant="secondary"
              onClick={handleQuickScan}
              disabled={!target.trim() || isScanning}
              className="flex-1 sm:flex-none"
              data-testid="button-quick-scan"
            >
              <Zap className="mr-2 h-4 w-4" />
              <span className="hidden sm:inline">{quickScanMutation.isPending ? "Scanning..." : "Quick Scan"}</span>
              <span className="sm:hidden">{quickScanMutation.isPending ? "Scanning..." : "Quick"}</span>
            </Button>
          </div>

          {scanResult && (
            <>
              {/* Scan Results */}
              <div className="bg-secondary rounded-lg">
                <div className="p-4 border-b border-border">
                  <div className="flex items-center justify-between">
                    <h4 className="font-medium flex items-center">
                      Open Ports Found
                    </h4>
                    <Badge className="bg-chart-3/20 text-chart-3" data-testid="text-open-ports-count">
                      {scanResult.openPorts.length} Active
                    </Badge>
                  </div>
                  <p className="text-sm text-muted-foreground mt-1">
                    Scanned {scanResult.totalPorts} ports on {scanResult.target} in {scanResult.scanDuration}ms
                  </p>
                </div>
                <div className="p-4 space-y-3 max-h-64 overflow-y-auto">
                  {scanResult.openPorts.length > 0 ? (
                    scanResult.openPorts.map((port) => (
                      <div key={port.port} className="flex items-center justify-between text-sm">
                        <div className="flex items-center space-x-3">
                          <Badge variant="outline" className="font-mono text-chart-3 border-chart-3">
                            {port.port}
                          </Badge>
                          <span>{port.service}</span>
                        </div>
                        <div className="text-right">
                          {port.banner && (
                            <div className="text-accent text-xs">{port.banner}</div>
                          )}
                          {port.version && (
                            <div className="text-muted-foreground text-xs">{port.version}</div>
                          )}
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="text-center text-muted-foreground py-4">
                      No open ports found in the specified range
                    </div>
                  )}
                </div>
              </div>
            </>
          )}

          {isScanning && (
            <div className="bg-secondary p-4 rounded-lg">
              <div className="flex items-center space-x-2">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-chart-3"></div>
                <span className="text-sm">
                  {quickScanMutation.isPending ? "Quick scanning common ports..." : "Scanning ports..."}
                </span>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ui/alert-dialog.ts.content:
import * as React from "react"
import * as AlertDialogPrimitive from "@radix-ui/react-alert-dialog"

import { cn } from "@/lib/utils"
import { buttonVariants } from "@/components/ui/button"

const AlertDialog = AlertDialogPrimitive.Root

const AlertDialogTrigger = AlertDialogPrimitive.Trigger

const AlertDialogPortal = AlertDialogPrimitive.Portal

const AlertDialogOverlay = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Overlay
    className={cn(
      "fixed inset-0 z-50 bg-black/80  data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className
    )}
    {...props}
    ref={ref}
  />
))
AlertDialogOverlay.displayName = AlertDialogPrimitive.Overlay.displayName

const AlertDialogContent = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Content>
>(({ className, ...props }, ref) => (
  <AlertDialogPortal>
    <AlertDialogOverlay />
    <AlertDialogPrimitive.Content
      ref={ref}
      className={cn(
        "fixed left-[50%] top-[50%] z-50 grid w-full max-w-lg translate-x-[-50%] translate-y-[-50%] gap-4 border bg-background p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:rounded-lg",
        className
      )}
      {...props}
    />
  </AlertDialogPortal>
))
AlertDialogContent.displayName = AlertDialogPrimitive.Content.displayName

const AlertDialogHeader = ({
  className,
  ...props
